import { MapContainer, TileLayer, Marker, Popup,} from 'react-leaflet'
import { Icon } from "leaflet";
import React, { useState, useEffect } from 'react';
import InputBase from '@mui/material/InputBase';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import Paper from '@mui/material/Paper';
import '../App.css'
import axios from 'axios';



export default function Map() {
const [location, setLocation] = useState(null);
const [searchInput, setSearchInput] = useState("");


useEffect(() => {
  if (searchInput) {
    axios
      .get(`https://geo.ipify.org/api/v2/country,city?apiKey=at_qaQb5z18XlN0etMkmTmNwTJbQzBnz&ipAddress=${searchInput}`)
      .then((res) => {
        setLocation(res.data);
        // Zoom in to marker location when it is set
        if (map && res.data.location) {
          const { lat, lng } = res.data.location;
          map.flyTo([lat, lng], 13, {
            duration: 1.5, // Adjust animation duration as desired
          });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }
}, [searchInput, map]);


console.log(location, 'location')

const handleSearch = (e) => {
  e.preventDefault();
  setSearchInput(e.target.value);
};

const mapCenter = location ? [location.location.lat, location.location.lng] : [45.4, -75.7];
const mapZoom = location ? 13 : 2;

  return (
    <div className='mapDiv'>
    <Paper
      className='input'
      component="form"
      sx={{ p: '2px 4px', display: 'flex', alignItems: 'center' }}
      onSubmit={(e) => {
        e.preventDefault();
        handleSearch();
      }}
    >
      <InputBase
        sx={{ ml: 1, flex: 1 }}
        placeholder="Enter IP Address"
        inputProps={{ 'aria-label': 'search IP address' }}
        value={searchInput}
        onChange={(e) => setSearchInput(e.target.value)}
      />
      <IconButton type="submit" sx={{ p: '10px' }} aria-label="search">
        <SearchIcon />
      </IconButton>
    </Paper>

    <Paper elevation={3} className='display-dashboard'>
      {location && (
        <>
          <text className='title2'>IP Address: {location.ip}</text>
          <Divider orientation="vertical" variant="middle" flexItem />
          <text className='title2'>Location: {location.location.city}, {location.location.country}</text>
          <Divider orientation="vertical" variant="middle" flexItem />
          <text className='title2'>Timezone: {location.location.timezone}</text>
          <Divider orientation="vertical" variant="middle" flexItem />
          <text className='title2'>ISP: {location.isp}</text>
        </>
      )}
    </Paper>

    <MapContainer
        center={mapCenter}
        zoom={mapZoom}
      scrollWheelZoom={false}
      className='leaflet-container'
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      {location && (
        <Marker position={[location.location.lat, location.location.lng]}>
          <Popup>
            <div>
              <p>IP Address: {location.ip}</p>
              <p>Location: {location.location.city}, {location.location.country}</p>
              <p>Timezone: {location.location.timezone}</p>
              <p>ISP: {location.isp}</p>
            </div>
          </Popup>
        </Marker>
      )}
    </MapContainer>
  </div>
  );
}
